CREATE TABLE IF NOT EXISTS users (id SERIAL PRIMARY KEY NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL);
CREATE TABLE products (id SERIAL PRIMARY KEY NOT NULL, name TEXT NOT NULL, price FLOAT NOT NULL, description TEXT, unit TEXT NOT NULL, barcode INTEGER NOT NULL, status INTEGER NOT NULL);
CREATE TABLE customers (id SERIAL PRIMARY KEY NOT NULL, name TEXT NOT NULL, address TEXT, tax INTEGER NOT NULL, status INTEGER NOT NULL);
CREATE TABLE suppliers (id SERIAL PRIMARY KEY NOT NULL, name TEXT NOT NULL, address TEXT, tax INTEGER NOT NULL, status INTEGER NOT NULL);
CREATE TABLE warehouses (id SERIAL PRIMARY KEY NOT NULL, name TEXT NOT NULL, address TEXT, status INTEGER NOT NULL);
CREATE TABLE order_list (id SERIAL PRIMARY KEY NOT NULL, uuid TEXT NOT NULL UNIQUE, number TEXT NOT NULL, date DATE NOT NULL, type INTEGER NOT NULL, from_id INTEGER NOT NULL, to_id INTEGER NOT NULL, status INTEGER NOT NULL);
CREATE TABLE order_items (id SERIAL PRIMARY KEY NOT NULL, order_id INTEGER NOT NULL, qty INTEGER NOT NULL, price FLOAT NOT NULL, product_id INTEGER NOT NULL);
CREATE TABLE warehouse_items (id SERIAL PRIMARY KEY NOT NULL, warehouse_id INTEGER NOT NULL, qty FLOAT NOT NULL, product_id INTEGER NOT NULL);
CREATE TABLE inventory_list (id SERIAL PRIMARY KEY NOT NULL, uuid TEXT NOT NULL UNIQUE, date DATE NOT NULL, warehouse_id INTEGER NOT NULL, status INTEGER NOT NULL);
CREATE TABLE inventory_items (id SERIAL PRIMARY KEY NOT NULL, inventory_id INTEGER NOT NULL, qty FLOAT, product_id INTEGER NOT NULL);
